(function() {
  var URL, urllite;

  urllite = require('../core');

  URL = urllite.URL;

  URL.prototype.normalize = function() {
    var m, pathname;
    pathname = this.pathname;
    while (m = /^(.*?)[^\/]+\/\.\.\/*(.*)$/.exec(pathname)) {
      pathname = "" + m[1] + m[2];
    }
    if (this.host && pathname.indexOf('..') !== -1) {
      throw new Error('Path is behind root.');
    }
    return urllite._createURL(this, {
      pathname: pathname
    });
  };

}).call(this);
